import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Container,
    Typography,
    Box,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Tabs,
    Tab,
    TextField,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    FormControl,
    InputLabel,
    Select,
    MenuItem
} from '@mui/material';

const FacultyDashboard = () => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
    const [activities, setActivities] = useState([]);
    const [students, setStudents] = useState([]);
    const [tabValue, setTabValue] = useState(0);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedStudent, setSelectedStudent] = useState('');
    const [behaviorData, setBehaviorData] = useState({
        behavior_type: '',
        remarks: ''
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [activitiesRes, studentsRes] = await Promise.all([
                    axios.get(`http://localhost:5000/api/faculty/${user.id}/activities`),
                    axios.get('http://localhost:5000/api/students') // You'll need to create this endpoint
                ]);
                setActivities(activitiesRes.data);
                setStudents(studentsRes.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, [user.id]);

    const handleTabChange = (event, newValue) => {
        setTabValue(newValue);
    };

    const handleOpenDialog = () => {
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedStudent('');
        setBehaviorData({ behavior_type: '', remarks: '' });
    };

    const handleBehaviorSubmit = async () => {
        try {
            await axios.post('http://localhost:5000/api/faculty/behavior', {
                student_id: selectedStudent,
                ...behaviorData
            });
            handleCloseDialog();
        } catch (error) {
            console.error('Error submitting behavior record:', error);
        }
    };

    return (
        <Container maxWidth="lg">
            <Box sx={{ mt: 4 }}>
                <Typography variant="h4" gutterBottom>
                    Welcome, {user.name}
                </Typography>
                <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 4 }}>
                    <Tabs value={tabValue} onChange={handleTabChange}>
                        <Tab label="Activities" />
                        <Tab label="Student Management" />
                    </Tabs>
                </Box>

                {tabValue === 0 && (
                    <TableContainer component={Paper}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Activity Name</TableCell>
                                    <TableCell>Type</TableCell>
                                    <TableCell>Date</TableCell>
                                    <TableCell>Description</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {activities.map((activity) => (
                                    <TableRow key={activity.activity_id}>
                                        <TableCell>{activity.name}</TableCell>
                                        <TableCell>{activity.type}</TableCell>
                                        <TableCell>{new Date(activity.date).toLocaleDateString()}</TableCell>
                                        <TableCell>{activity.description}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}

                {tabValue === 1 && (
                    <Box>
                        <Button
                            variant="contained"
                            onClick={handleOpenDialog}
                            sx={{ mb: 2 }}
                        >
                            Add Behavior Record
                        </Button>
                        <TableContainer component={Paper}>
                            <Table>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Student Name</TableCell>
                                        <TableCell>Department</TableCell>
                                        <TableCell>Year</TableCell>
                                        <TableCell>Actions</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {students.map((student) => (
                                        <TableRow key={student.student_id}>
                                            <TableCell>{student.name}</TableCell>
                                            <TableCell>{student.department}</TableCell>
                                            <TableCell>{student.year}</TableCell>
                                            <TableCell>
                                                <Button
                                                    variant="outlined"
                                                    onClick={() => {
                                                        setSelectedStudent(student.student_id);
                                                        handleOpenDialog();
                                                    }}
                                                >
                                                    Add Record
                                                </Button>
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Box>
                )}
            </Box>

            <Dialog open={openDialog} onClose={handleCloseDialog}>
                <DialogTitle>Add Behavior Record</DialogTitle>
                <DialogContent>
                    <FormControl fullWidth sx={{ mt: 2 }}>
                        <InputLabel>Behavior Type</InputLabel>
                        <Select
                            value={behaviorData.behavior_type}
                            onChange={(e) => setBehaviorData({ ...behaviorData, behavior_type: e.target.value })}
                            label="Behavior Type"
                        >
                            <MenuItem value="Good Conduct">Good Conduct</MenuItem>
                            <MenuItem value="Late Submission">Late Submission</MenuItem>
                            <MenuItem value="Excellent Performance">Excellent Performance</MenuItem>
                            <MenuItem value="Attendance Issue">Attendance Issue</MenuItem>
                            <MenuItem value="Disruptive Behavior">Disruptive Behavior</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField
                        fullWidth
                        label="Remarks"
                        multiline
                        rows={4}
                        value={behaviorData.remarks}
                        onChange={(e) => setBehaviorData({ ...behaviorData, remarks: e.target.value })}
                        sx={{ mt: 2 }}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Cancel</Button>
                    <Button onClick={handleBehaviorSubmit} variant="contained">
                        Submit
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default FacultyDashboard;
