import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Container,
    Typography,
    Box,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Tabs,
    Tab,
    TextField,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    IconButton,
    Chip
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import PeopleIcon from '@mui/icons-material/People';

const FacultyDashboard = () => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
    const [activities, setActivities] = useState([]);
    const [students, setStudents] = useState([]);
    const [tabValue, setTabValue] = useState(0);
    const [openDialog, setOpenDialog] = useState(false);
    const [openEnrollDialog, setOpenEnrollDialog] = useState(false);
    const [selectedStudent, setSelectedStudent] = useState('');
    const [selectedActivity, setSelectedActivity] = useState(null);
    const [behaviorData, setBehaviorData] = useState({
        behavior_type: '',
        remarks: ''
    });
    const [activityData, setActivityData] = useState({
        activity_id: '',
        name: '',
        type: '',
        date: '',
        description: ''
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [activitiesRes, studentsRes] = await Promise.all([
                    axios.get(`http://localhost:5000/api/faculty/${user.id}/activities`),
                    axios.get('http://localhost:5000/api/students')
                ]);
                setActivities(activitiesRes.data);
                setStudents(studentsRes.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, [user.id]);

    const handleTabChange = (event, newValue) => {
        setTabValue(newValue);
    };

    const handleOpenDialog = () => {
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedStudent('');
        setBehaviorData({ behavior_type: '', remarks: '' });
    };

    const handleOpenEnrollDialog = (activity) => {
        setSelectedActivity(activity);
        setOpenEnrollDialog(true);
    };

    const handleCloseEnrollDialog = () => {
        setOpenEnrollDialog(false);
        setSelectedActivity(null);
    };

    const handleBehaviorSubmit = async () => {
        try {
            await axios.post('http://localhost:5000/api/faculty/behavior', {
                student_id: selectedStudent,
                ...behaviorData
            });
            handleCloseDialog();
        } catch (error) {
            console.error('Error submitting behavior record:', error);
        }
    };

    const handleActivitySubmit = async () => {
        try {
            await axios.post('http://localhost:5000/api/activity', {
                ...activityData,
                faculty_id: user.id
            });
            setActivityData({
                activity_id: '',
                name: '',
                type: '',
                date: '',
                description: ''
            });
            // Refresh activities
            const response = await axios.get(`http://localhost:5000/api/faculty/${user.id}/activities`);
            setActivities(response.data);
        } catch (error) {
            console.error('Error creating activity:', error);
        }
    };

    const handleEnrollStudent = async (studentId) => {
        try {
            await axios.post('http://localhost:5000/api/activity/enroll', {
                student_id: studentId,
                activity_id: selectedActivity.activity_id
            });
            handleCloseEnrollDialog();
        } catch (error) {
            console.error('Error enrolling student:', error);
        }
    };

    return (
        <Container maxWidth="lg">
            <Box sx={{ mt: 4 }}>
                <Typography variant="h4" gutterBottom>
                    Welcome, {user.name}
                </Typography>
                <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 4 }}>
                    <Tabs value={tabValue} onChange={handleTabChange}>
                        <Tab label="Activities" />
                        <Tab label="Student Management" />
                    </Tabs>
                </Box>

                {tabValue === 0 && (
                    <Box>
                        <Button
                            variant="contained"
                            startIcon={<AddIcon />}
                            onClick={() => setOpenDialog(true)}
                            sx={{ mb: 2 }}
                        >
                            Create New Activity
                        </Button>
                        <TableContainer component={Paper}>
                            <Table>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Activity Name</TableCell>
                                        <TableCell>Type</TableCell>
                                        <TableCell>Date</TableCell>
                                        <TableCell>Enrolled Students</TableCell>
                                        <TableCell>Actions</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {activities.map((activity) => (
                                        <TableRow key={activity.activity_id}>
                                            <TableCell>{activity.name}</TableCell>
                                            <TableCell>{activity.type}</TableCell>
                                            <TableCell>{new Date(activity.date).toLocaleDateString()}</TableCell>
                                            <TableCell>
                                                <Chip
                                                    icon={<PeopleIcon />}
                                                    label={activity.enrolled_students}
                                                    onClick={() => handleOpenEnrollDialog(activity)}
                                                />
                                            </TableCell>
                                            <TableCell>
                                                <Button
                                                    variant="outlined"
                                                    onClick={() => handleOpenEnrollDialog(activity)}
                                                >
                                                    Manage Students
                                                </Button>
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Box>
                )}

                {tabValue === 1 && (
                    <Box>
                        <Button
                            variant="contained"
                            onClick={handleOpenDialog}
                            sx={{ mb: 2 }}
                        >
                            Add Behavior Record
                        </Button>
                        <TableContainer component={Paper}>
                            <Table>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Student Name</TableCell>
                                        <TableCell>Department</TableCell>
                                        <TableCell>Year</TableCell>
                                        <TableCell>Actions</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {students.map((student) => (
                                        <TableRow key={student.student_id}>
                                            <TableCell>{student.name}</TableCell>
                                            <TableCell>{student.department}</TableCell>
                                            <TableCell>{student.year}</TableCell>
                                            <TableCell>
                                                <Button
                                                    variant="outlined"
                                                    onClick={() => {
                                                        setSelectedStudent(student.student_id);
                                                        handleOpenDialog();
                                                    }}
                                                >
                                                    Add Record
                                                </Button>
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Box>
                )}
            </Box>

            {/* Create Activity Dialog */}
            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
                <DialogTitle>Create New Activity</DialogTitle>
                <DialogContent>
                    <TextField
                        fullWidth
                        label="Activity ID"
                        value={activityData.activity_id}
                        onChange={(e) => setActivityData({ ...activityData, activity_id: e.target.value })}
                        margin="normal"
                    />
                    <TextField
                        fullWidth
                        label="Activity Name"
                        value={activityData.name}
                        onChange={(e) => setActivityData({ ...activityData, name: e.target.value })}
                        margin="normal"
                    />
                    <FormControl fullWidth margin="normal">
                        <InputLabel>Activity Type</InputLabel>
                        <Select
                            value={activityData.type}
                            onChange={(e) => setActivityData({ ...activityData, type: e.target.value })}
                            label="Activity Type"
                        >
                            <MenuItem value="Academic">Academic</MenuItem>
                            <MenuItem value="Sports">Sports</MenuItem>
                            <MenuItem value="Cultural">Cultural</MenuItem>
                            <MenuItem value="Workshop">Workshop</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField
                        fullWidth
                        label="Date"
                        type="date"
                        value={activityData.date}
                        onChange={(e) => setActivityData({ ...activityData, date: e.target.value })}
                        margin="normal"
                        InputLabelProps={{ shrink: true }}
                    />
                    <TextField
                        fullWidth
                        label="Description"
                        multiline
                        rows={4}
                        value={activityData.description}
                        onChange={(e) => setActivityData({ ...activityData, description: e.target.value })}
                        margin="normal"
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Cancel</Button>
                    <Button onClick={handleActivitySubmit} variant="contained">
                        Create
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Enroll Students Dialog */}
            <Dialog open={openEnrollDialog} onClose={handleCloseEnrollDialog} maxWidth="md" fullWidth>
                <DialogTitle>Enroll Students in {selectedActivity?.name}</DialogTitle>
                <DialogContent>
                    <TableContainer>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Student Name</TableCell>
                                    <TableCell>Department</TableCell>
                                    <TableCell>Year</TableCell>
                                    <TableCell>Action</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {students.map((student) => (
                                    <TableRow key={student.student_id}>
                                        <TableCell>{student.name}</TableCell>
                                        <TableCell>{student.department}</TableCell>
                                        <TableCell>{student.year}</TableCell>
                                        <TableCell>
                                            <Button
                                                variant="outlined"
                                                onClick={() => handleEnrollStudent(student.student_id)}
                                            >
                                                Enroll
                                            </Button>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseEnrollDialog}>Close</Button>
                </DialogActions>
            </Dialog>

            {/* Behavior Record Dialog */}
            <Dialog open={openDialog} onClose={handleCloseDialog}>
                <DialogTitle>Add Behavior Record</DialogTitle>
                <DialogContent>
                    <FormControl fullWidth sx={{ mt: 2 }}>
                        <InputLabel>Behavior Type</InputLabel>
                        <Select
                            value={behaviorData.behavior_type}
                            onChange={(e) => setBehaviorData({ ...behaviorData, behavior_type: e.target.value })}
                            label="Behavior Type"
                        >
                            <MenuItem value="Good Conduct">Good Conduct</MenuItem>
                            <MenuItem value="Late Submission">Late Submission</MenuItem>
                            <MenuItem value="Excellent Performance">Excellent Performance</MenuItem>
                            <MenuItem value="Attendance Issue">Attendance Issue</MenuItem>
                            <MenuItem value="Disruptive Behavior">Disruptive Behavior</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField
                        fullWidth
                        label="Remarks"
                        multiline
                        rows={4}
                        value={behaviorData.remarks}
                        onChange={(e) => setBehaviorData({ ...behaviorData, remarks: e.target.value })}
                        sx={{ mt: 2 }}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Cancel</Button>
                    <Button onClick={handleBehaviorSubmit} variant="contained">
                        Submit
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default FacultyDashboard;
